package assignments;

public class DefaultValue {

	
	
	int a;
	char c;
	float f;
	double d;
	long l;
	short s;
	boolean b;
	byte bt;
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		DefaultValue dv = new DefaultValue();
		
		System.out.println("DefaultValue of integer : "+dv.a);
		System.out.println("DefaultValue of Char : "+dv.c);

		System.out.println("DefaultValue of float : "+dv.f);

		System.out.println("DefaultValue of double : "+dv.d);

		System.out.println("DefaultValue of long : "+dv.l);

		System.out.println("DefaultValue of shor : "+dv.s);

		System.out.println("DefaultValue of boolean : "+dv.b);

		System.out.println("DefaultValue of byte : "+dv.bt);


	}

}
