package assignments;

public class ForDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i,j;
		
		System.out.println("display ************* 1-25 ****************");
		
		for(i=1;i<=25;i++) {
			System.out.println(i);
		}
		
		for(j=25;j>=0;j--) {
			System.out.println(j);
		}

	}

}
